    <script src='https://cdnjs.cloudflare.com/ajax/libs/jqueryui/1.12.1/jquery-ui.min.js'></script>
    <script>

        $(function () {
            $(".datepicker").datepicker({
                dateFormat: "dd-mm-yy"
                , duration: "fast"
            });
        });

        $(document.body).on('click', '.add-to-trip', function () {
            $('#car_detail').modal('show'); // show bootstrap modal when complete loaded
            $(".htmlDisplay").html('<h3 align=center class=text-warning><i class="fa fa-spinner fa-spin" style="font-size:24px"></i> დაელოდეთ...</h3>');
            let id = $(this).data('id');
            $.ajax({
                url: "{{ route('user.add-to-trip') }}",
                method: "POST",
                data: {
                    _token: '{{ csrf_token() }}',
                    'location_id': id,
                },
                success: function (response) {
                        $('.htmlDisplay').html(response.html);
                },
                error: function (xhr, ajaxOptions, thrownError) {
                    if (xhr.status == 401) {
                        Swal.fire({
                            title: 'შეცდომა!',
                            text: "რჩეულებში დასამატებლად გთხოვთ გაიაროთ ავტორიზაცია!",
                            icon: 'error',
                            showCancelButton: false
                        }).then((result) => {
                            if (result.value) {
                                location.href = "{{ route('login') }}"
                            }
                        })
                    }
                }
            })
        })

        $(document.body).on('click', '.save-btn', function () {
            let tripId = $('#trip_id').val();
            let tripTitle = $('#trip_title').val()
            let locationId = $('#location_id').val();
            $.ajax({
                url: "{{ route('user.add-to-trip.detail') }}",
                method: "POST",
                data: {
                    _token: '{{ csrf_token() }}',
                    'trip_id': tripId,
                    'trip_title': tripTitle,
                    'location_id': locationId,
                },
                success: function (response) {
                    if (response.status == 0) {
                        Swal.fire('წარმატებული!', response.msg, 'success');
                        $('#car_detail').modal('hide');
                    }
                },
                error: function (xhr, ajaxOptions, thrownError) {
                    if (xhr.status == 422) { // when status code is 422, it's a validation issue
                        $('.errors').html('');
                        $.each(xhr.responseJSON.errors, function (i, error) {
                            $('.' + i + '_err').html(error);
                        });
                    }
                    $this.html('შენახვა');
                    $this.prop('disabled', false);
                }
            })
        })
    </script>
